import React, { useState } from 'react';
import image1 from '../images/galary/p01.jpg';
import image2 from '../images/galary/p02.jpg';
import image3 from '../images/galary/p03.jpg';
import image4 from '../images/galary/p04.jpg';
import image5 from '../images/galary/p05.jpg';
import image6 from '../images/galary/p06.jpg';
import image7 from '../images/galary/p07.jpg';
import image8 from '../images/galary/p08.jpg';
import image9 from '../images/galary/p09.jpg';
import image10 from '../images/galary/p10.jpg';

const images = [
  image1,
  image2,
  image3,
  image4,
  image5,
  image6,
  image7,
  image8,
  image9,
  image10
];

const ImageGallery = () => {
  const [showAllImages, setShowAllImages] = useState(false);

  const handleToggleImages = () => {
    setShowAllImages(!showAllImages);
  };

  const visibleImages = showAllImages ? images : images.slice(0, 4);

  return (<>


    <div className="container mx-auto px-4 pm-4 mt-[30px]">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {visibleImages.map((imageUrl, index) => (
          <div key={index} className="relative aspect-w-1 aspect-h-1">
            <img src={imageUrl} alt={`Image ${index}`} className="object-cover w-full h-full rounded-lg" />
          </div>
        ))}
      </div>

    </div>

    <div className="text-center ">
      <button
        onClick={handleToggleImages}
        className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 focus:outline-none focus:bg-blue-600"
      >
        {showAllImages ? 'Show Less' : 'Show More'}
      </button>
    </div>

  </>

  );
};

export default ImageGallery;
