import CustomSlider from "../component/CustomCarousel";
import "../App.css";

import React, { useEffect, useState } from 'react';
import firebase from '../firebase/firebase'; // Update the path as per your file structure

import 'firebase/compat/firestore';
import 'firebase/compat/storage';

function HeroSection() {
    const [imageUrls, setImageUrls] = useState([]);


    useEffect(() => {
        const fetchImages = async () => {
            try {
                const storageRef = firebase.storage().ref();
                const imagesRef = storageRef.child('hero_section'); // Update the path to your folder

                // List all images in the folder
                const imageList = await imagesRef.listAll();

                // Get download URLs for each image
                const urls = await Promise.all(
                    imageList.items.map(async (imageRef) => {
                        return await imageRef.getDownloadURL();
                    })
                );

                setImageUrls(urls);
            } catch (error) {
                console.error('Error fetching images:', error);
            }
        };

        fetchImages();
    }, []);


    return <div>
        <div>

            <div className="relative">
                <CustomSlider>
                    {imageUrls.map((image, index) => {
                        return (
                            <div key={index} className="relative">
                                <div>
                                    {imageUrls.map((url, index) => (
                                        <img key={index} src={url} alt={`Image ${index}`} />
                                    ))}
                                </div>
                                {/* <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-white text-center">
                                    <div className="bg-black bg-opacity-75 p-4 rounded-md">
                                        <h1>{image.description}</h1>
                                    </div>
                                </div> */}
                            </div>
                        );
                    })}
                </CustomSlider>
            </div>

        </div>
    </div>;
}

export default HeroSection;
