import React, { useEffect, useState } from 'react';
import { TableContainer, Table, TableHead, TableBody, TableRow, TableCell, IconButton, Dialog, DialogTitle, DialogContent, DialogContentText, Button, TextField } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
// import { Button, TextField } from '@mui/material';
import firebase from '../firebase/firebase';

const HeroSectionAdmin = () => {
    const [imageUrls, setImageUrls] = useState([]);
    const [editDialogOpen, setEditDialogOpen] = useState(false);
    const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
    const [selectedId, setSelectedId] = useState(null);
    const [editedData, setEditedData] = useState({});
    //upload image
    const [file, setFile] = useState(null);
    const [fileName, setFileName] = useState('');

    const handleFileChange = (e) => {
      const selectedFile = e.target.files[0];
      setFile(selectedFile);
    };


    useEffect(() => {

        fetchImages();
    }, []);

    const fetchImages = async () => {
        try {
            const storageRef = firebase.storage().ref();
            const imagesRef = storageRef.child('hero_section');
            const imageList = await imagesRef.listAll();
            // Get download URLs for each image
            const urls = await Promise.all(
                imageList.items.map(async (imageRef) => {
                    return await imageRef.getDownloadURL();
                })
            );
            setImageUrls(urls);
        } catch (error) {
            console.error('Error fetching images:', error);
        }
    };

    const handleUpload = async () => {
        try {
            if (!file) {
                console.error('No file selected.');
                return;
            }

            const storageRef = firebase.storage().ref();
            const fileRef = storageRef.child(`hero_section/${fileName}`);
            await fileRef.put(file);
            console.log('File uploaded successfully.');

            // Clear input field after successful upload
            setFile(null);
            setFileName('');
            fetchImages();
        } catch (error) {
            console.error('Error uploading file:', error);
        }
    };
    const handleEditClick = (id) => {
        // Handle edit action here
    };
    const handleDeleteClick = (id) => {
        setSelectedId(id);
        setDeleteDialogOpen(true);
    };

    const handleEditSave = () => {
        // Handle edit save action here
        setEditDialogOpen(false);
    };

    const handleDeleteConfirm = async () => {
        try {
            const imageUrl = imageUrls[selectedId];
            // Delete image from Firebase Storage
            const storageRef = firebase.storage().refFromURL(imageUrl);
            await storageRef.delete();

            // Update state to remove deleted image
            setImageUrls((prevImageUrls) =>
                prevImageUrls.filter((url, index) => index !== selectedId)
            );
        } catch (error) {
            console.error('Error deleting image:', error);
        }
        setDeleteDialogOpen(false);
    };

    return (
        <><div className='flex justify-center items-center border rounded p-4 w-[300px]'>
        <input
          type="file"
          accept="image/*"
          onChange={handleFileChange}
          className="hidden"
          id="upload-file"
        />
        <label htmlFor="upload-file" className="cursor-pointer mr-2">
          Choose File
        </label>
        <Button
          variant="contained"
          color="primary"
          onClick={handleUpload}
          disabled={!file}
        >
          Upload
        </Button>
      </div>
            <TableContainer>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Image</TableCell>
                            <TableCell>Action</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {imageUrls.map((url, index) => (
                            <TableRow key={index}>
                                <TableCell>
                                    <img src={url} alt={`Image ${index}`} width="50" height="50" />
                                </TableCell>
                                <TableCell>
                                    <IconButton onClick={() => handleEditClick(index)}>
                                        <EditIcon />
                                    </IconButton>
                                    <IconButton onClick={() => handleDeleteClick(index)}>
                                        <DeleteIcon />
                                    </IconButton>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            {/* Edit Dialog */}
            <Dialog open={editDialogOpen} onClose={() => setEditDialogOpen(false)}>
                <DialogTitle>Edit Image</DialogTitle>
                <DialogContent>
                    <DialogContentText>Edit the image details:</DialogContentText>
                    {/* Add input fields for editing */}
                    <Button onClick={handleEditSave}>Save</Button>
                </DialogContent>
            </Dialog>

            {/* Delete Confirmation Dialog */}
            <Dialog open={deleteDialogOpen} onClose={() => setDeleteDialogOpen(false)}>
                <DialogTitle>Delete Image</DialogTitle>
                <DialogContent>
                    <DialogContentText>Are you sure you want to delete this image?</DialogContentText>
                    <Button onClick={handleDeleteConfirm}>Confirm</Button>
                    <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
                </DialogContent>
            </Dialog>
        </>
    );
};

export default HeroSectionAdmin;
