import React, { useEffect, useState } from "react";
import CustomSlider from "../component/CustomCarousel";
import firebase from "../firebase/Firebase"; // Import firebase object

export default function HeroSection() {
  const images = [
    // Your image data
  ];

  const [imageUrls, setImageUrls] = useState([]);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const storageRef = firebase.storage().ref();
        const imagesRef = storageRef.child("images");

        // List all images in the "images" folder
        const imageList = await imagesRef.listAll();

        // Get download URLs for each image
        const urls = await Promise.all(
          imageList.items.map(async (imageRef) => {
            return await imageRef.getDownloadURL();
          })
        );

        setImageUrls(urls);
      } catch (error) {
        console.error("Error fetching images:", error);
      }
    };

    fetchImages();
  }, []);

  return (
    <div className="relative">
      <CustomSlider>
        {images.map((image, index) => {
          return (
            <div key={index} className="relative">
              {/* eslint-disable-next-line no-undef */}
              <img src={imageUrls[index]} alt={`Image ${index}`} /> {/* Display image */}
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-white text-center">
                <div className="bg-black bg-opacity-75 p-4 rounded-md">
                  <h1>{image.description}</h1>
                </div>
              </div>
            </div>
          );
        })}
      </CustomSlider>
    </div>
  );
}
