import React, { useState, useEffect } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/database';//AdminLogin
import AdminLogin from '../admin/AdminLogin';

function App() {
  const [user, setUser] = useState(null);
  const [images, setImages] = useState([]);

  useEffect(() => {
    const unsubscribe = firebase.auth().onAuthStateChanged((user) => {
      setUser(user);
    });
    return unsubscribe;
  }, []);

  useEffect(() => {
    // Fetch images from Firebase Database based on user's folder
    const fetchImages = async () => {
      if (user) {
        const snapshot = await firebase.database().ref(`images/${user.uid}`).once('value');
        if (snapshot.exists()) {
          setImages(snapshot.val());
        }
      }
    };

    fetchImages();
  }, [user]);

  const handleLogout = () => {
    firebase.auth().signOut();
  };

  return (
    <div>
      {user ? (
        <div>
          <p>Welcome, {user.email}</p>
          <button onClick={handleLogout}>Logout</button>
          <table>
            <thead>
              <tr>
                <th>Image Name</th>
                <th>URL</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(images).map((key) => (
                <tr key={key}>
                  <td>{images[key].name}</td>
                  <td>{images[key].url}</td>
                  <td>
                    <button>Edit</button>
                    <button>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <AdminLogin />
      )}
    </div>
  );
}
