import React, { useState, useEffect } from 'react';
import firebase from '../firebase/firebase';
import 'firebase/compat/firestore';
import 'firebase/compat/storage';

const ImageGallery = () => {
  const [imageUrls, setImageUrls] = useState([]);
  const [visibleImageCount, setVisibleImageCount] = useState(8);

  useEffect(() => {
    // Initialize Firebase app if not already initialized
    if (!firebase.apps.length) {
      firebase.initializeApp(yourFirebaseConfig);
    }

    // Fetch images from Firebase Storage
    const fetchImages = async () => {
      try {
        const storageRef = firebase.storage().ref();
        const imagesRef = storageRef.child('gallery');
        const imageList = await imagesRef.listAll();
        const urls = await Promise.all(
          imageList.items.map(async (imageRef) => {
            return await imageRef.getDownloadURL();
          })
        );
        setImageUrls(urls);
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };

    // Fetch images when the component mounts
    fetchImages();

    // Clean up Firebase app instance to prevent memory leaks
    return () => {
      if (firebase.apps.length > 0) {
        firebase.app().delete();
      }
    };
  }, []);

  const handleShowMore = () => {
    setVisibleImageCount(prevCount => prevCount + 4); // Increase visible image count by 4
  };

  return (
    <div className="container mx-auto">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {imageUrls.slice(0, visibleImageCount).map((url, index) => (
          <div key={index} className="aspect-w-1 aspect-h-1">
            <img src={url} alt={`Image ${index}`} className="object-cover w-full h-full rounded-lg" />
          </div>
        ))}
      </div>
      {visibleImageCount < imageUrls.length && (
        <button
          onClick={handleShowMore}
          className="block mx-auto mt-4 px-4 py-2 bg-blue-500 text-white rounded-md shadow hover:bg-blue-600 focus:outline-none focus:ring focus:ring-blue-400"
        >
          Show More
        </button>
      )}
    </div>
  );
};

export default ImageGallery;
