import React, { useState } from 'react';
import backgroundImage from '../images/hero1004.jpeg'; 

export default function Contacts() {

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobileNumber: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // You can add your form submission logic here
    console.log(formData);
    // Reset form fields after submission
    setFormData({
      name: '',
      email: '',
      mobileNumber: ''
    });
  };

  return (
    <div
    style={{backgroundImage: `url(${backgroundImage})`,backgroundSize: 'cover', backgroundPosition: 'center' }}>
      <div 
      className="border p-10 w-[40%] h-[30%] mx-auto max-w-md relative" 
      style={{ backgroundImage: 'url("/path/to/your/image.jpg")', backgroundSize: 'cover', backgroundPosition: 'center' }}
    >
      <div className="absolute inset-0 bg-black opacity-50"></div>
      <form onSubmit={handleSubmit} className="relative z-10">
        <div>
          <label htmlFor="name" className="text-white">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            className='border border-gray-300 px-3 py-2 rounded-md focus:outline-none focus:border-blue-500 text-black'
          />
        </div>
        <div>
          <label htmlFor="email" className="text-white">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            className='border border-gray-300 px-3 py-2 rounded-md focus:outline-none focus:border-blue-500 text-black'
          />
        </div>
        <div>
          <label htmlFor="mobileNumber" className="text-white">Mobile Number:</label>
          <input
            type="tel"
            id="mobileNumber"
            name="mobileNumber"
            value={formData.mobileNumber}
            onChange={handleChange}
            pattern="[0-9]{10}"
            required
            className='border border-gray-300 px-3 py-2 rounded-md focus:outline-none focus:border-blue-500 text-black'
          />
        </div>
        <div className="flex justify-center items-center h-full pt-4">
          <button
            type="submit"
            className='border border-gray-300 px-3 py-2 rounded-md focus:outline-none focus:border-blue-500 text-center text-white'
          >
            Submit
          </button>
        </div>    
      </form>
    </div>
    </div>
  );
}
