import React, { useState } from 'react';

const images = [
  'image1.jpg',
  'image2.jpg',
  'image3.jpg',
  'image4.jpg',
  'image5.jpg',
  'image6.jpg',
  'image7.jpg',
  'image8.jpg',
  'image9.jpg',
  'image10.jpg',
  // Add more image URLs as needed
];

const ImageGallery = () => {
  const [showMore, setShowMore] = useState(false);
  const imagesToShow = showMore ? images : images.slice(0, 4);

  return (
    <div className="container mx-auto px-4">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {imagesToShow.map((imageUrl, index) => (
          <div key={index} className="relative aspect-w-1 aspect-h-1">
            <img src={imageUrl} alt={`Image ${index}`} className="object-cover w-full h-full rounded-lg" />
          </div>
        ))}
      </div>
      {!showMore && (
        <button
          onClick={() => setShowMore(true)}
          className="block mx-auto mt-4 bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 focus:outline-none focus:bg-blue-600"
        >
          Show More
        </button>
      )}
    </div>
  );
};

export default ImageGallery;
