import React, { useState } from 'react';


export default function Contacts() {

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobileNumber: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // You can add your form submission logic here
    console.log(formData);
    // Reset form fields after submission
    setFormData({
      name: '',
      email: '',
      mobileNumber: ''
    });
  };

  return (
    <div className='border p-10 w-[40%] h-[30%] mx-auto max-w-md'>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            className='border border-gray-300 px-3 py-2 rounded-md focus:outline-none focus:border-blue-500'
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            className='border border-gray-300 px-3 py-2 rounded-md focus:outline-none focus:border-blue-500'
          />
        </div>
        <div>
          <label htmlFor="mobileNumber">Mobile Number:</label>
          <input
            type="tel"
            id="mobileNumber"
            name="mobileNumber"
            value={formData.mobileNumber}
            onChange={handleChange}
            pattern="[0-9]{10}"
            required
            className='border border-gray-300 px-3 py-2 rounded-md focus:outline-none focus:border-blue-500'
          />

        </div>
        <div className="flex justify-center items-center h-full pt-4">
          <button
            type="submit"
            className='border border-gray-300 px-3 py-2 rounded-md focus:outline-none focus:border-blue-500 text-center'
          >
            Submit
          </button>
        </div>    </form>
    </div>
  );

}
