import CustomSlider from "../component/CustomCarousel";
import "../App.css";
import React, { useEffect, useState } from 'react';
import firebase from '../firebase/firebase';
import 'firebase/compat/firestore';
import 'firebase/compat/storage';

function HeroSection() {
    const [imageUrls, setImageUrls] = useState([]);


    useEffect(() => {
        const fetchImages = async () => {
            try {
                const storageRef = firebase.storage().ref();
                const imagesRef = storageRef.child('hero_section');
                const imageList = await imagesRef.listAll();
                // Get download URLs for each image
                const urls = await Promise.all(
                    imageList.items.map(async (imageRef) => {
                        return await imageRef.getDownloadURL();
                    })
                );
                setImageUrls(urls);
            } catch (error) {
                console.error('Error fetching images:', error);
            }
        };
        fetchImages();
    }, []);


    return (
        <div className="h-[100vh]">
            <div className="relative ">
                <div className="absolute top-0 left-0 w-full h-full flex flex-col justify-center items-center z-10">
                    <div className="absolute top-0 left-0 w-full h-full flex flex-col justify-center items-center z-10">
                        <div className="max-w-screen-lg mx-auto px-4 lg:px-0 lg:w-full pb-0 bg-white rounded-lg shadow-lg backdrop-filter backdrop-blur-lg bg-opacity-10">
                            <h1 className="text-4xl font-bold mb-6 text-gray-900 text-center">Welcome to PrimeSofa and Furnishings</h1>
                            <p className="text-lg mb-8 text-gray-800 items-center">
                                Your premier destination for exquisite, comfortable furniture. Our curated collection boasts handcrafted sofas, chairs, tables, and accessories meticulously designed to elevate the aesthetics and functionality of your living space.
                            </p>
                            <p className="text-lg mb-8 text-gray-800 items-center">
                                At PrimeSofa and Furnishings, we take pride in our commitment to utilizing only the finest quality materials and expert craftsmanship to fashion pieces that are as durable as they are elegant. Whether you're envisioning a timeless leather sofa or a contemporary sectional, our extensive range caters to diverse tastes and preferences.
                            </p>
                        </div>
                    </div>

                </div>


                <CustomSlider>
                    {imageUrls.map((url, index) => (
                        <div key={index} className="relative">
                            <img src={url} alt={`Image ${index}`} />
                        </div>
                    ))}
                </CustomSlider>
            </div>




            <p>Work Description</p>
            {/* //our services */}
            <div className="bg-gray-100 py-12">
                <div className="container mx-auto px-4">
                    <div className="max-w-screen-xl mx-auto">
                        <div className="text-center">
                            <h2 className="text-3xl font-semibold mb-4">Our Story</h2>
                            <p className="text-lg text-gray-800 mb-6">
                                Primesofa and Furnishings is your destination for sustainable interior furnishings in India. We are passionate about crafting eco-friendly furniture that combines comfort and style. Our skilled craftsmen use eco-conscious materials and practices to create high-quality pieces that enhance your living or work space.
                            </p>
                            <p className="text-lg text-gray-800">
                                Based in Bengaluru, we deliver our unique and sustainable furniture to homes and businesses across India. Explore our collection today and experience the perfect blend of comfort, style, and sustainability with Primesofa and Furnishings.
                            </p>
                        </div>
                    </div>
                </div>
            </div>


        </div>
    );
}

export default HeroSection;
